void naive_sgemm_cuda_invoke_in_cpp(float* C, float* A, float* B, long size) {
    float* d_C;
    float* d_A;
    float* d_B;

    size_t matrixSize = size * size * sizeof(float);

    cudaMalloc((void**)&d_C, matrixSize);
    cudaMalloc((void**)&d_A, matrixSize);
    cudaMalloc((void**)&d_B, matrixSize);

    cudaMemcpy(d_A, A, matrixSize, cudaMemcpyHostToDevice);
    cudaMemcpy(d_B, B, matrixSize, cudaMemcpyHostToDevice);
    cudaMemcpy(d_C, C, matrixSize, cudaMemcpyHostToDevice);

    dim3 blockDim(16, 16);
    dim3 gridDim((size + blockDim.x - 1) / blockDim.x, (size + blockDim.y - 1) / blockDim.y);

    naive_sgemm_kernel<<<gridDim, blockDim>>>(d_C, d_A, d_B, size);

    cudaMemcpy(C, d_C, matrixSize, cudaMemcpyDeviceToHost);

    cudaFree(d_C);
    cudaFree(d_A);
    cudaFree(d_B);
}

