void vectorMatrixMult_cpu_invoke_in_cpp(long int totalPixels, int availablePixels, int outPixelOffset, float* matrix, float* vector, float* out) {
    // Define device pointers
    float* d_matrix;
    float* d_vector;
    float* d_out;

    // Allocate device memory
    cudaMalloc((void**)&d_matrix, totalPixels * availablePixels * sizeof(float));
    cudaMalloc((void**)&d_vector, totalPixels * sizeof(float));
    cudaMalloc((void**)&d_out, availablePixels * sizeof(float));

    // Copy data to the device
    cudaMemcpy(d_matrix, matrix, totalPixels * availablePixels * sizeof(float), cudaMemcpyHostToDevice);
    cudaMemcpy(d_vector, vector, totalPixels * sizeof(float), cudaMemcpyHostToDevice);

    // Determine dimensions for kernel execution
    int threadsPerBlock = 256; 
    int blocksPerGrid = (availablePixels + threadsPerBlock - 1) / threadsPerBlock;

    // Call the CUDA kernel
    vectorMatrixMult<<<blocksPerGrid, threadsPerBlock>>>(totalPixels, availablePixels, outPixelOffset, d_matrix, d_vector, d_out);

    // Copy the result back to host
    cudaMemcpy(out, d_out, availablePixels * sizeof(float), cudaMemcpyDeviceToHost);

    // Free device memory
    cudaFree(d_matrix);
    cudaFree(d_vector);
    cudaFree(d_out);
}

