void compute_b_minus_Rx_cuda_invoke_in_cpp(double* out, double* x, double* b, double* cotans, int* neighbors, int meshStride, int n) {
    double* d_out;
    double* d_x;
    double* d_b;
    double* d_cotans;
    int* d_neighbors;

    cudaMalloc((void**)&d_out, n * sizeof(double));
    cudaMalloc((void**)&d_x, n * sizeof(double));
    cudaMalloc((void**)&d_b, n * sizeof(double));
    cudaMalloc((void**)&d_cotans, n * meshStride * sizeof(double));
    cudaMalloc((void**)&d_neighbors, n * meshStride * sizeof(int));

    cudaMemcpy(d_x, x, n * sizeof(double), cudaMemcpyHostToDevice);
    cudaMemcpy(d_b, b, n * sizeof(double), cudaMemcpyHostToDevice);
    cudaMemcpy(d_cotans, cotans, n * meshStride * sizeof(double), cudaMemcpyHostToDevice);
    cudaMemcpy(d_neighbors, neighbors, n * meshStride * sizeof(int), cudaMemcpyHostToDevice);

    int blockSize = 256; 
    int numBlocks = (n + blockSize - 1) / blockSize;

    compute_b_minus_Rx<<<numBlocks, blockSize>>>(d_out, d_x, d_b, d_cotans, d_neighbors, meshStride, n);

    cudaMemcpy(out, d_out, n * sizeof(double), cudaMemcpyDeviceToHost);

    cudaFree(d_out);
    cudaFree(d_x);
    cudaFree(d_b);
    cudaFree(d_cotans);
    cudaFree(d_neighbors);
}

