#include <cmath>

void binarize_input_cpu_invoke_in_cpp (float * input, int n, int size, float * binary){
    // Allocate memory for CPU input and binary
    float* h_input = (float*) malloc(n*size*sizeof(float));
    float* h_binary = (float*) malloc(n*size*sizeof(float));

    // Copy memory from original input and binary to CPU input and binary
    memcpy(h_input, input, n*size*sizeof(float));
    memcpy(h_binary, binary, n*size*sizeof(float));

    // Binariaze input
    for(int s = 0; s < size; s++){
        float mean = 0;
        for(int i = 0 ; i < n ; ++ i ) {
            mean += std::abs(h_input[i * size + s]);
        }
        mean = mean/n;
        for(int i=0; i<n; ++i){
            h_binary [i*size + s] = (h_input [i*size + s] > 0)? mean : -mean ;
        }
    }

    // Copy memory from CPU binary to original binary
    memcpy(binary, h_binary, n*size*sizeof(float));

    // Free allocated memory
    free(h_input);
    free(h_binary);
}

