void cuda_laplace_filter_invoke_in_cpp(float *Img, float *laplace, float _dz, float _dx, int npml, int nnz, int nnx) {

    // Calculate size
    size_t size = nnz * nnx * sizeof(float);

    // Allocate memory on GPU
    float* d_Img;
    cudaMalloc((void**)&d_Img, size);
    float* d_laplace;
    cudaMalloc((void**)&d_laplace, size);

    // Copy data from host to device
    cudaMemcpy(d_Img, Img, size, cudaMemcpyHostToDevice);

    // Set up dimensions for the kernel launch
    dim3 threadsPerBlock(16, 16);
    dim3 numBlocks((nnz + threadsPerBlock.x - 1) / threadsPerBlock.x, (nnx + threadsPerBlock.y - 1) / threadsPerBlock.y);

    // Launch the kernel
    cuda_laplace_filter<<<numBlocks, threadsPerBlock>>>(d_Img, d_laplace, _dz, _dx, npml, nnz, nnx);

    // Copy the results back from device to host
    cudaMemcpy(laplace, d_laplace, size, cudaMemcpyDeviceToHost);

    // Free up the allocated memory on device
    cudaFree(d_Img);
    cudaFree(d_laplace);
}

