void kernel_sum_backward_invoke_in_cpp(float* db, float* dout, int r, int c) {
    // Allocate Host copies
    float h_db[c], h_dout[r*c];

    // Copy data from device
    cudaMemcpy(h_db, db, c * sizeof(float), cudaMemcpyDeviceToHost);
    cudaMemcpy(h_dout, dout, r * c * sizeof(float), cudaMemcpyDeviceToHost);

    int N = c;

    for (int tid = 0; tid < N ; tid++) {
        for (int i = 0; i < r; i++) {
            h_db[tid] += h_dout[i * c + tid];
        }
    }

    // Copy results back to device memory
    cudaMemcpy(db, h_db, c * sizeof(float), cudaMemcpyHostToDevice);
}

