void cpu_Adam_step_invoke_in_cuda(float * grad, float * data, float * m, float * v, bool decay, float weight_decay, float beta1, float beta2, float eps, float step_size, int varsize) {
    // Allocate memory on device
    float* d_grad; 
    float* d_data; 
    float* d_m; 
    float* d_v;

    cudaMalloc((void**)&d_grad, varsize * sizeof(float));
    cudaMalloc((void**)&d_data, varsize * sizeof(float));
    cudaMalloc((void**)&d_m, varsize * sizeof(float));
    cudaMalloc((void**)&d_v, varsize * sizeof(float));

    // Copy data from host to device
    cudaMemcpy(d_grad, grad, varsize * sizeof(float), cudaMemcpyHostToDevice);
    cudaMemcpy(d_data, data, varsize * sizeof(float), cudaMemcpyHostToDevice);
    cudaMemcpy(d_m, m, varsize * sizeof(float), cudaMemcpyHostToDevice);
    cudaMemcpy(d_v, v, varsize * sizeof(float), cudaMemcpyHostToDevice);

    // Call the CUDA kernel
    cuda_Adam_step_kernel<<<varsize, 1>>>(d_grad, d_data, d_m, d_v, decay, weight_decay, beta1, beta2, eps, step_size, varsize);

    // Copy data back from device to host
    cudaMemcpy(grad, d_grad, varsize * sizeof(float), cudaMemcpyDeviceToHost);
    cudaMemcpy(data, d_data, varsize * sizeof(float), cudaMemcpyDeviceToHost);
    cudaMemcpy(m, d_m, varsize * sizeof(float), cudaMemcpyDeviceToHost);
    cudaMemcpy(v, d_v, varsize * sizeof(float), cudaMemcpyDeviceToHost);

    // Free memory on device
    cudaFree(d_grad);
    cudaFree(d_data);
    cudaFree(d_m);
    cudaFree(d_v);
}

