void conv2_cuda_invoke_in_cpp( float * A, float * kernel, int inputSize, int depth, int kernelSize, int stride, int pad, float * B, int outputSize ) {
    float *d_A, *d_kernel, *d_B;

    cudaMalloc((void**) &d_A, inputSize * inputSize * depth * sizeof(float));
    cudaMalloc((void**) &d_kernel, kernelSize * kernelSize * depth * sizeof(float));
    cudaMalloc((void**) &d_B, outputSize * outputSize * depth * sizeof(float));

    cudaMemcpy(d_A, A, inputSize * inputSize * depth * sizeof(float), cudaMemcpyHostToDevice);
    cudaMemcpy(d_kernel, kernel, kernelSize * kernelSize * depth * sizeof(float), cudaMemcpyHostToDevice);

    dim3 threadsPerBlock(outputSize, outputSize);
    dim3 numBlocks(1, 1);
    if (outputSize * outputSize > 512) {
        threadsPerBlock.x = 512;
        threadsPerBlock.y = 512;
        numBlocks.x = ceil(double(outputSize * outputSize) / double(threadsPerBlock.x));
        numBlocks.y = ceil(double(outputSize * outputSize) / double(threadsPerBlock.y));
    }

    conv2<<<numBlocks, threadsPerBlock>>>(d_A, d_kernel, inputSize, depth, kernelSize, stride, pad, d_B, outputSize);

    cudaMemcpy(B, d_B, outputSize * outputSize * depth * sizeof(float), cudaMemcpyDeviceToHost);

    cudaFree(d_A);
    cudaFree(d_kernel);
    cudaFree(d_B);
}

